import {
  GoogleMap,
  InfoWindowF,
  MarkerF,
  InfoWindow,
  Marker,
  LoadScript,
} from "@react-google-maps/api";
import React, { useEffect, useState } from "react";
// import ImageTask from "./ImageTask";
import axios from "axios";


const markers = [
  {
    id: 1,
    name: "Chicago, Illinois",
    position: { lat: 41.881832, lng: -87.623177 },
  },
  {
    id: 2,
    name: "Denver, Colorado",
    position: { lat: 39.739235, lng: -104.99025 },
  },
  {
    id: 3,
    name: "Los Angeles, California",
    position: { lat: 34.052235, lng: -118.243683 },
  },
  {
    id: 4,
    name: "New York, New York",
    position: { lat: 40.712776, lng: -74.005974 },
  },
];

function GoogleMapCom({ tasks }) {
  let zoom = 16;
  const [dataCar, setDataCar] = useState([]);

  const getDataLocationCar = (marker) => {
    axios.post('http://172.20.10.4:3001/web/user/getLocationCar', {})
      .then(res => {
        let {status, result} = res.data;
        console.log(res.data);
        status && setDataCar(result);
      })
      .catch(err => {
        console.error(err);
      });
  };

  useEffect(() => {
    const interval = setInterval(() => {
      getDataLocationCar();
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  // const handleOnLoad = (map) => {
  //   const bounds = new google.maps.LatLngBounds();
  //   markers.forEach(({ position }) => bounds.extend(position));
  //   map.fitBounds(bounds);
  // };

  const status_check = (status) => {
    if (status === 0) {
      return "รอรับเรื่อง";
    } else if (status === 1) {
      return "ดำเนินการ";
    } else if (status === 2) {
      return "เสร็จสิ้น";
    }
  };

  return (
    <>
      <LoadScript googleMapsApiKey="AIzaSyBHBTkH9fICG5hTL1xNFkyLXaQGyZU6fek">
        <GoogleMap
          options={{
            streetViewControl: true,
            zoom: zoom,
          }}
          // onLoad={handleOnLoad}
          center={{ lat: 14.160833268480362, lng: 101.34863184895529 }}
          // onClick={() => setActiveMarker(null)}
          mapContainerStyle={{ width: "100vw", height: "100vh" }}
        >
          {tasks.map((val, idx) => (
                        <MarkerF
                            key={idx}
                            position={{ lat: val.driver_lat, lng: val.driver_long }}
                            // onClick={() => handleActiveMarker(_id)}
                        >

                        </MarkerF>
                    ))}
        </GoogleMap>
      </LoadScript>
    </>
  );
}

export default GoogleMapCom;
